import React, { Component } from 'react'
import { DragSource } from 'react-dnd';
import { compose } from 'redux';
import {connect} from 'react-redux'
import { decreaseAttempt } from '../Actions/actions';

const itemSource = {
    beginDrag(props) {
        console.log("dragging")
        const itemVal = {
            item: props.item
        }
        return itemVal

    },
    endDrag(props, monitor) {
        if(!monitor.didDrop()) {
            // this.props.dispatch(decreaseAttempt())
            console.log(props)
        }

       return props.handleDrop(props.id)
    }
}



function collect(connect, monitor) {
    return {
        connectDragSource: connect.dragSource(),
        connectDragPreview: connect.dragPreview(),
        isDragging: monitor.isDragging()
    }
}
function forType(props) {
    return props.type;
}

class DragElement extends Component {
    constructor(props) {
        super(props);
    }
  render() {
    const { items, isDragging, connectDragSource } = this.props;
    const opacity = isDragging ? 0 : 1;
    // if(attemptsLeft <= 0) {
    //     return <div>Game Over</div>
    // }
        return connectDragSource(
           <div className="main-container">		
           	<div>
                <div className="stuff" style={{opacity}}>{this.props.item}</div>
           	</div>
           </div> 
    )
  }
}


function mapDispatchToProps(dispatch){
    return {
        decrease : () => dispatch(decreaseAttempt())
    }
}


export default compose(
    DragSource(forType, itemSource, collect),
    connect(null, mapDispatchToProps)
)(DragElement)
