const initState = { data: {} }

export default (state=initState, action) => {
	switch(action.type) {
		case 'STORE_DATA':
		{
			function randomIndex() {
				return Math.floor(Math.random() * 4);

			}
			// let randomArr = []
			// let obj = {

			// }
			// for(var i=1; i <= 3; i++) {
			// 	randomArr.push(action.value.Fruits[randomIndex()])
			// }
			// console.log(randomArr)

			const keys = Object.keys(action.value);

			const objectToBeReturend = {}
			var randomArr = [];
			var key;
			for(let i = 0; i < keys.length; i++) {
				key = keys[i];
				var valuesArr;
				for(let j = 0; j < 3; j++) {
					valuesArr = action.value[keys[i]];
					randomArr.push(valuesArr[randomIndex()])
				}
				objectToBeReturend.keys[i] = randomArr;
				randomArr = []
			}

			console.log(objectToBeReturend);

			return {
				...state,
				data: action.value
			}
		}

	}
}
