import React, { Component } from 'react'
import { DragSource } from 'react-dnd';
import { compose } from 'redux';

const itemSource = {
    beginDrag(props) {
        console.log("dragging")
        return props.item

    },
    endDrag(props) {
        console.log("stopped")
       return props.handleDrop(props.id)
    },
}

const handleDrop = (id) => {
    console.log("deleted")
}

function collect(connect, monitor) {
    return {
        connectDragSource: connect.dragSource(),
        connectDragPreview: connect.dragPreview(),
        isDragging: monitor.isDragging()
    }
}

class DragElement extends Component {
  render() {
    const { items, isDragging, connectDragSource } = this.props;
	const opacity = isDragging ? 0 : 1;
        return connectDragSource(
           <div className="main-container">																																																																																																																																																																																																																																																																																																																																																																																																																																																																						
           	<div>
                <div className="stuff" style={{opacity}}>{this.props.item}</div>
           	</div>
           </div> 
    )
  }
}


export default DragSource("item", itemSource, collect)(DragElement)
