const initState = { data: {} }

export default (state=initState, action) => {
	switch(action.type) {
		case 'STORE_DATA':
		{
			function randomIndex() {
				return Math.floor(Math.random() * 4);

			}
			let randomArr = []
			for(var i=1; i <= 3; i++) {
				randomArr.push(action.value.Fruits[randomIndex()])
			}
			console.log(randomArr)
			return {
				...state,
				data: action.value
			}
		}

	}
}
