import React, { Component } from 'react';
import { connect } from 'react-redux'

class Kids extends Component {
    constructor(props) {
        super(props);
    }

    render() {
		const { items } = this.props;
		console.log(items)
        return (
           <div className="main-container">
           <h1>Kinder Joy</h1>																																																																																																																																																																																																																																																																																																																																																																																																																																																																						
           	<div className="stuff-container">
           		{
				items && items.map(item => <div className="stuff" draggable="true">{item}</div>)	
				   }
           	</div>
           	<table>
           	  <tr>
           	  	<th>Animals</th>
           	  	<th>Fruits</th>
           	  	<th>Numbers</th>
           	  	<th>Colours</th>
           	  </tr>
           	</table>
           </div> 
        );
    }
}

const mapStateToProps = (state) => {
	console.log(state)
	return {
		items: state.data
	}
}

export default connect(mapStateToProps)(Kids);
